class PagamentoTipo extends DaoPagamentoTipo
{

    const ATIVO = 1;
    const INATIVO = 2;
    const EXCLUIDO = 3;

    const APLICACAO = 1;
    const MANUTENCAO = 2;
    const REMOCAO = 3;


    public function getStatus() {
        switch ($this->status) {

            case PagamentoTipo::ATIVO:
                return 'Ativo';

            case PagamentoTipo::INATIVO:
                return 'Inativo';

            case PagamentoTipo::EXCLUIDO:
                return 'Excluido';

            default:
                return 'Indefinido';
        }
    }

    public function getTipo() {
        switch ($this->tipo) {

            case ProcedimentoTipo::APLICACAO:
                return 'Aplicação';

            case ProcedimentoTipo::MANUTENCAO:
                return 'Manutenção';

            case ProcedimentoTipo::REMOCAO:
                return 'Remoção';

            default:
                return 'Indefinido';
        }
    }
}